public class Part1 {
  String dna = "";
  public int findStopCodon(String dna, int startIndex, String stopCodon){
    int currentIndex = dna.indexOf(stopCodon, startIndex);
    while (currentIndex != -1){
      if ((currentIndex - startIndex) % 3 == 0){
          return currentIndex;
        }
      currentIndex = dna.indexOf(stopCodon, currentIndex+1);
    }
    return dna.length();
    }
  
    public void testFindStopCodon (){
    int startIndex = 0;
    System.out.println(findStopCodon(dna, startIndex, "taa"));
    System.out.println(findStopCodon(dna, startIndex, "tga"));
    System.out.println(findStopCodon(dna, startIndex, "tag"));
    System.out.println(findStopCodon(dna, startIndex, "txg"));
    System.out.println(findGene(dna));
  }
  
  public String findGene(String dna){
    int startCodon = dna.indexOf("atg");
    if (startCodon == -1){ return ""; }
    int stopCodonTaa = findStopCodon(dna, startCodon, "taa");
    int stopCodonTga = findStopCodon(dna, startCodon, "tga");
    int stopCodonTag = findStopCodon(dna, startCodon, "tag");
    int temp = Math.min(stopCodonTaa, stopCodonTga) != -1 ? Math.min(stopCodonTaa, stopCodonTga) : Math.max(stopCodonTaa, stopCodonTga);
    int stopCodon = Math.min(stopCodonTag, temp) != -1 ? Math.min(stopCodonTag, temp) : Math.max(stopCodonTag, temp);
    if (stopCodon == -1 || stopCodon == dna.length()) { return ""; }
    return dna.substring(startCodon, stopCodon+3);
  }
  
  public void testFindGene(){
    String dna1 = "gatcctccatatacaacggtatctccacctcaggtttagatctcaacaacggaaccattgc";
    String dna2 = "atggatcctccatatacaacggtatctccacctcaggttagatctcaacaacggaaccattaagc";
    String dna3 = "tggatcctccatatgacaacggtatctccacctcaggtttagatctcaacaacggaaccattgc";
    String dna4 = "atggatcct";
    String dna5 = "atggatcctccatatacaacggtatctccacctcaggtttagatctcaacaacggaaccattgca";
    System.out.println(findGene(dna1));
    System.out.println(findGene(dna2));
    System.out.println(findGene(dna3));
    System.out.println(findGene(dna4));
    System.out.println(findGene(dna5));
    }
  
  public void printAllGenes(String dna){
    int currentPos = dna.indexOf("atg");
    while (currentPos != -1){
      String gene = findGene(dna.substring(currentPos));
      if (gene.length() == 0) { break; }
      System.out.println(gene);
      currentPos = currentPos + gene.length();
    }
  }
  
  public void testLast(){
    printAllGenes(dna);
  }
}

